import React from 'react';
import '../styles/menu.css';

const menuItems = [
  { id: 1, category: 'Hookah', name: 'Classic Apple', price: '$15' },
  { id: 2, category: 'Hookah', name: 'Mint Chill', price: '$18' },
  { id: 3, category: 'Hookah', name: 'Berry Mix', price: '$20' },
  { id: 4, category: 'Drinks', name: 'Mojito', price: '$10' },
  { id: 5, category: 'Drinks', name: 'Whiskey Sour', price: '$12' },
  { id: 6, category: 'Snacks', name: 'Cheese Plate', price: '$14' },
  { id: 7, category: 'Snacks', name: 'Fruit Mix', price: '$9' },
];

const Menu = ({ onBack }) => {
  return (
    <div className="menu-screen">
      <button className="back-button" onClick={onBack}>
        ‚Üê Back
      </button>
      
      <h2>Our Menu</h2>
      
      <div className="menu-categories">
        <h3>Hookah</h3>
        {menuItems
          .filter(item => item.category === 'Hookah')
          .map(item => (
            <div key={item.id} className="menu-item">
              <span className="item-name">{item.name}</span>
              <span className="item-price">{item.price}</span>
            </div>
          ))}
          
        <h3>Drinks</h3>
        {menuItems
          .filter(item => item.category === 'Drinks')
          .map(item => (
            <div key={item.id} className="menu-item">
              <span className="item-name">{item.name}</span>
              <span className="item-price">{item.price}</span>
            </div>
          ))}
          
        <h3>Snacks</h3>
        {menuItems
          .filter(item => item.category === 'Snacks')
          .map(item => (
            <div key={item.id} className="menu-item">
              <span className="item-name">{item.name}</span>
              <span className="item-price">{item.price}</span>
            </div>
          ))}
      </div>
    </div>
  );
};

export default Menu;
